def greet(name: str) -> str:
    return f"Hello, {name}!"

def fibonacci(n: int) -> list:
    seq = [0, 1]
    for _ in range(n - 2):
        seq.append(seq[-1] + seq[-2])
    return seq

print(greet("Charlie"))
print("Fibonacci sequence:", fibonacci(10))

